export const callStackAReg = (
  stack: any,
  {fn}: {[key: string]: any},
  {a}: Stack,
) => fn(stack, a)
export const callARegStack = (
  stack: any,
  {fn}: {[key: string]: any},
  {a}: Stack,
) => fn(a, stack)
export const callStack = (stack: any, {fn}: {[key: string]: any}, _: Stack) =>
  fn(stack)
import type {
  StateRef,
  Compute,
  MovValueToRegister,
  MovValueToStore,
  MovStoreToRegister,
  MovStoreToStore,
  MovRegisterToStore,
  Stack,
} from './index.h'
import {nextStepID} from './id'
import {EFFECT, REG_A, SAMPLER, STACK, STORE} from './tag'
import type {BarrierPriorityTag} from './kernel'
import {callStack} from './caller'

const cmd = <Type extends 'compute' | 'mov'>(
  type: Type,
  data: any,
  priority?: BarrierPriorityTag | false,
  batch?: boolean,
) => {
  const result: {
    id: string
    type: Type
    data: any
    order?: {
      priority: BarrierPriorityTag
      barrierID?: number
    }
  } = {
    id: nextStepID(),
    type,
    data,
  }
  if (priority) {
    result.order = {priority}
    if (batch) result.order.barrierID = ++nextBarrierID
  }
  return result
}

let nextBarrierID = 0

export const mov: {
  <T>(data: {
    from: 'value'
    store: T
    target: StateRef
    batch?: boolean
    priority?: BarrierPriorityTag
  }): MovValueToStore<T>
  <T>(data: {
    from: 'value'
    to: 'stack' | 'a' | 'b'
    store: T
    batch?: boolean
    priority?: BarrierPriorityTag
  }): MovValueToRegister<T>
  (data: {
    from: 'a' | 'b' | 'stack'
    target: StateRef
    batch?: boolean
    priority?: BarrierPriorityTag
  }): MovRegisterToStore
  (data: {
    from: 'a' | 'b' | 'stack'
    to: 'a' | 'b' | 'stack'
    batch?: boolean
    priority?: BarrierPriorityTag
  }): MovRegisterToStore
  (data: {
    store: StateRef
    target: StateRef
    batch?: boolean
    priority?: BarrierPriorityTag
  }): MovStoreToStore
  (data: {
    store: StateRef
    to: 'stack' | 'a' | 'b'
    batch?: boolean
    priority?: BarrierPriorityTag | false
  }): MovStoreToRegister
  (data: {
    store: StateRef
    batch?: boolean
    priority?: BarrierPriorityTag | false
  }): MovStoreToRegister
  // (data: {
  //   from?: 'value' | 'store' | 'stack' | 'a' | 'b'
  //   to?: 'stack' | 'a' | 'b' | 'store'
  //   store?: StateRef
  //   target?: StateRef
  // }): Mov
} = ({
  from = STORE,
  store,
  target,
  to = target ? STORE : STACK,
  batch,
  priority,
}: {
  from?: 'value' | 'store' | 'stack' | 'a' | 'b'
  to?: 'stack' | 'a' | 'b' | 'store'
  store?: StateRef
  target?: StateRef
  batch?: boolean
  priority?: BarrierPriorityTag | false
}) => cmd('mov', {from, store, to, target}, priority, batch)

export const compute = <
  LocalValues extends {[key: string]: any} = {[key: string]: any},
>({
  fn,
  batch,
  priority,
  safe = false,
  filter = false,
  pure = false,
}: {
  fn?: (data: any, scope: LocalValues, stack: Stack) => any
  batch?: boolean
  priority?: BarrierPriorityTag | false
  safe?: boolean
  filter?: boolean
  pure?: boolean
}): Compute => cmd('compute', {fn, safe, filter, pure}, priority, batch)

export const filter = ({
  fn,
  pure,
}: {
  fn(data: any, scope: {[key: string]: any}, stack: Stack): any
  pure?: boolean
}) => compute({fn, filter: true, pure})

export const run = ({
  fn,
}: {
  fn(data: any, scope: {[key: string]: any}, stack: Stack): any
}) => compute({fn, priority: EFFECT})

export const calc = <
  LocalValues extends {[key: string]: any} = {[key: string]: any},
>(
  fn: (data: any, scope: LocalValues, stack: Stack) => any,
  filter?: boolean,
  isEffect?: boolean,
) => compute({fn, safe: true, filter, priority: isEffect && EFFECT})

/**
 * `read(ref, true, true)`: **reg.stack** with **sampler** batch
 *
 * `read(ref, true, false)`: **reg.stack** without batch
 *
 * `read(ref, false, true)`: **reg.a** with **sampler** batch
 *
 * `read(ref, false, false)`: **reg.a** without batch
 *
 */
export const read = (
  store: StateRef,
  toStack?: boolean,
  samplerPriority?: boolean,
) =>
  mov({
    store,
    to: toStack ? STACK : REG_A,
    priority: samplerPriority && SAMPLER,
    batch: true,
  })

export const userFnCall = (
  fn: (data: any, scope: {[key: string]: any}, stack: Stack) => any = callStack,
  isFilter?: boolean,
) => compute({fn, pure: true, filter: isFilter})

export const step = {mov, compute, filter, run}
import type {StateRef, StateRefOp} from './index.h'
import {nextStepID} from './id'
import {add} from './collection'

export const createStateRef = (current?: any): StateRef => ({
  id: nextStepID(),
  current,
})
export const readRef = ({current}: StateRef | {current: any}) => current

export const addRefOp = (ref: StateRef, op: StateRefOp) => {
  if (!ref.before) ref.before = []
  add(ref.before!, op as any)
}
import {Node, NodeUnit} from './index.h'
import {getGraph, getOwners, getLinks, getSubscribers, getMeta} from './getter'
import {is} from './is'
import {removeItem} from './collection'
import {CROSSLINK} from './tag'

const removeFromNode = (currentNode: Node, targetNode: Node) => {
  removeItem(currentNode.next, targetNode)
  removeItem(getOwners(currentNode), targetNode)
  removeItem(getLinks(currentNode), targetNode)
}
const clearNodeNormalized = (
  targetNode: Node,
  deep: boolean,
  isDomainUnit: boolean,
) => {
  targetNode.next.length = 0
  targetNode.seq.length = 0
  //@ts-expect-error
  targetNode.scope = null
  let currentNode
  let list = getLinks(targetNode)
  while ((currentNode = list.pop())) {
    removeFromNode(currentNode, targetNode)
    if (
      deep ||
      (isDomainUnit && getMeta(targetNode, 'op') !== 'sample') ||
      currentNode.family.type === CROSSLINK
    ) {
      clearNodeNormalized(
        currentNode,
        deep,
        getMeta(currentNode, 'op') !== 'on' && isDomainUnit,
      )
    }
  }
  list = getOwners(targetNode)
  while ((currentNode = list.pop())) {
    removeFromNode(currentNode, targetNode)
    if (isDomainUnit && currentNode.family.type === CROSSLINK) {
      clearNodeNormalized(
        currentNode,
        deep,
        getMeta(currentNode, 'op') !== 'on' && isDomainUnit,
      )
    }
  }
}
const clearMap = (map: Map<any, any> | Set<any>) => map.clear()
export const clearNode = (
  graphite: NodeUnit,
  {
    deep,
  }: {
    deep?: boolean
  } = {},
) => {
  let isDomainUnit = false
  //@ts-expect-error
  if (graphite.ownerSet) graphite.ownerSet.delete(graphite)
  if (is.store(graphite)) {
    clearMap(getSubscribers(graphite))
  } else if (is.domain(graphite)) {
    isDomainUnit = true
    const history = graphite.history
    clearMap(history.events)
    clearMap(history.effects)
    clearMap(history.stores)
    clearMap(history.domains)
  }
  clearNodeNormalized(getGraph(graphite), !!deep, isDomainUnit)
}
import type {Subscription, NodeUnit} from './index.h'
import {clearNode} from './clearNode'

export const createSubscription = (node: NodeUnit): Subscription => {
  const result = () => clearNode(node)
  result.unsubscribe = result
  return result
}
import {processArgsToConfig} from './config'
import {createNode} from './createNode'
import type {Subscription, NodeUnit, Cmd} from './index.h'
import {createSubscription} from './subscription'
import {assertNodeSet, assertTarget} from './is'

export const createLinkNode = (
  parent: NodeUnit | NodeUnit[],
  child: NodeUnit | NodeUnit[],
  node?: Array<Cmd | false | void | null>,
  op?: string,
  scopeFn?: Function,
) =>
  createNode({
    node,
    parent,
    child,
    scope: {fn: scopeFn},
    meta: {op},
    family: {owners: [parent, child], links: child},
    regional: true,
  })
export const forward = (opts: {
  from: NodeUnit | NodeUnit[]
  to: NodeUnit | NodeUnit[]
  meta?: Record<string, any>
}): Subscription => {
  const method = 'forward'
  const [{from, to}, config] = processArgsToConfig(opts, true)
  assertNodeSet(from, method, '"from"')
  assertNodeSet(to, method, '"to"')
  assertTarget(method, to, 'to')
  return createSubscription(
    createNode({
      parent: from,
      child: to,
      meta: {op: method, config},
      family: {},
      regional: true,
    }),
  )
}
import {run} from './step'
import {callStack} from './caller'
import {createNode} from './createNode'
import {Subscription, NodeUnit} from './index.h'
import {createSubscription} from './subscription'
import {assert} from './throw'
import {isFunction} from './is'

export const watchUnit = (
  unit: NodeUnit,
  handler: (payload: any) => any,
): Subscription => {
  assert(isFunction(handler), '.watch argument should be a function')
  return createSubscription(
    createNode({
      scope: {fn: handler},
      node: [run({fn: callStack})],
      parent: unit,
      meta: {op: 'watch'},
      family: {owners: unit},
      regional: true,
    }),
  )
}
