import React from 'react';
import { useGate, useStore } from "effector-react";
import {
    Image, Typography
  } from 'antd';

import { course } from '../../entities';

import { SCoursePage } from "./styles";
import { useParams } from 'react-router';

const CoursePage: React.FC = () => {
  const { CoursePageGate, $currentCourse } = course;

  const { id } = useParams();

  useGate(CoursePageGate, id);

  const { Title } = Typography;

  const currentCourse = useStore($currentCourse);

  if (!currentCourse) {
    return null;
  }

  return (
    <SCoursePage>
        <Title level={2} ellipsis>{currentCourse.title}</Title>
        <Image
            // width={200}
            src={currentCourse.previewImg?.filePath ? `/${currentCourse.previewImg?.filePath}` : ''}
        />
    </SCoursePage>
  );
}

export default CoursePage;
