import { PreviewImg } from "../../api/rest/courses/types";

type CourseItemProps = {
  title: string;
  previewImg?: PreviewImg | null;
};

const CourseItem: React.FC<CourseItemProps> = ({ title, previewImg }) => {
  // useGate(CourseListPageGate);
  console.log(previewImg);

  // const courseList = useStore($courseList);
  return <SCourseItem cover={<img alt="example" src={previewImg?.filePath ? previewImg?.filePath : './placeholder.jpeg'} />}>{title}</SCourseItem>
};

export default CourseItem;
